    await Tool.insertMany(defaultTools);
    defaultTools.forEach(tool => this.registerTool(tool));
  }

  public registerTool(tool: Tool) {
    this.tools.set(tool.id, tool);

    if (!this.categoryIndex.has(tool.category)) {
      this.categoryIndex.set(tool.category, new Set());
    }
    this.categoryIndex.get(tool.category)?.add(tool.id);
  }

  public getTool(id: string): Tool | undefined {
    return this.tools.get(id);
  }

  public getToolsByCategory(category: ToolCategory): Tool[] {
    const toolIds = this.categoryIndex.get(category) || new Set();
    return Array.from(toolIds).map(id => this.tools.get(id)!);
  }

