    const tool = this.getTool(toolId);
    if (!tool) throw new Error('Tool not found');

    // Verify user has access to this tool
    if (!user.subscribedTools.includes(toolId) && !tool.isFree) {
      throw new Error('Tool access not authorized');
    }

